import React, { Component } from "react";
import {
  View,
  Text,
  ListView,
  StyleSheet,
  FlatList,
  Section,
  TouchableHighlight,
  TextInput,
} from "react-native";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from "react-native-table-component";
import Modal from "react-native-modal";
import { ListItem, Button } from "react-native-elements";

import courseList from "./data/courses.json";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);

    this._deleteCourse = this._deleteCourse.bind(this);
    this.state = {
      tableHead: ["Name", "Preis", "Beschreibung"],
      courseList: courseList,
      isModalVisible: false,
    };
  }

  componentDidMount() {}
  toggleModal = () => {
    this.setState({ isModalVisible: !this.state.isModalVisible });
  };

  _addCourse() {
    console.log("Added");
  }
  _deleteCourse(i) {
    this.setState({
      courseList: this.state.people.filter((course, i) => i !== index),
    });
    //console.log("Deleted");
  }
  _editCourse() {
    console.log("Edited");
  }

  render() {
    const courseList = this.state.courseList;

    return (
      <View>
        {courseList.map((l, i) => (
          <ListItem key={i} bottomDivider>
            <ListItem.Content>
              <ListItem.Title>{"Name"}</ListItem.Title>
              <ListItem.Subtitle>{l.name}</ListItem.Subtitle>
              <ListItem.Title>{"Preis"}</ListItem.Title>
              <ListItem.Subtitle>{l.price}</ListItem.Subtitle>
              <ListItem.Title>{"Beschreibung"}</ListItem.Title>
              <ListItem.Subtitle>{l.description}</ListItem.Subtitle>
              <Button
                title="Delete"
                size={15}
                onPress={this._deleteCourse(i)}
              ></Button>
              <Button title="Edit" onPress={this._editCourse}></Button>
            </ListItem.Content>
          </ListItem>
        ))}
      </View>
    );
  }
}

const styles = StyleSheet.create({
  header: {
    height: 60,
    backgroundColor: "orange",
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
  },
  contentContainer: {
    backgroundColor: "white",
  },
  item: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderBottomColor: "grey",
    alignItems: "center",
  },
  marginLeft: {
    marginLeft: 5,
  },
  menu: {
    width: 20,
    height: 2,
    backgroundColor: "#111",
    margin: 2,
    borderRadius: 3,
  },
  text: {
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    marginLeft: 10,
  },
  text1: {
    textAlign: "left",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "black",
    // marginLeft: "auto",
  },
  text2: {
    textAlign: "center",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "blue",
    marginLeft: "auto",
  },
  text3: {
    textAlign: "right",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "orange",
    marginLeft: "auto",
  },

  textInput: {
    width: "90%",
    marginLeft: 10,
    marginRight: 10,
    marginBottom: 30,
    borderColor: "gray",
    borderBottomWidth: 2,
    fontSize: 16,
  },
  modalView: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "center",
    justifyContent: "center",
  },
  touchableHighlight: {
    backgroundColor: "white",
    marginVertical: 10,
    alignSelf: "stretch",
    alignItems: "center",
  },
});

export default Fitnessinfo;

