import React, { Component } from "react";
import {
  Button,
  View,
  Text,
  TouchableOpacity,
  ListView,
  StyleSheet,
  FlatList,
  Section,
  Modal,
  TouchableHighlight,
  TextInput,
} from "react-native";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
} from "react-native-table-component";
import courseList from "./data/courses.json";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);
    this.setModalVisible = this.setModalVisible.bind(this);

    this.state = {
      courseList: courseList,
      HeadTable: ["Name", "Preis", "Beschreibung"],
      isModalVisible: false,
    };
  }
  setModalVisible = (bool) => {
    this.setState({ isModalVisible: bool });
  };
  _deleteCourse(index) {
    const { courseList } = this.state;
    this.setState({
      courseList: courseList.filter((fitness) => fitness.id !== id),
    });
    console.log("Deleted");
    return;
  }

  renderItem = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.marginLeft}>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
      </View>
      <View>
        <Text style={styles.text}>{item.name}</Text>
        <Text style={styles.text}>{item.price}</Text>
        <Text style={styles.text}>{item.description}</Text>
      </View>
    </View>
  );

  render() {
    const state = this.state;
    const goalInput = (enteredText) => {
      setEnteredGoal(enteredText);
    };
    const addCourse = () => {
      <View>
        <Modal visible={true}>
          <TextInput
            style={styles.textInput}
            //defaultValue={this.state.inputPrice}
            editable={true}
          />
        </Modal>
      </View>;
    };
    return (
      <View style={styles.contentContainer}>
        <Button title="Add" onPress={() => this.addCourse}></Button>
        <Table borderStyle={{ borderWidth: 1, borderColor: "#000000" }}>
          <Row data={state.HeadTable} />
          {courseList.map((fitness, index) => (
            <Row key={index} data={fitness} />
          ))}
        </Table>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  header: {
    height: 60,
    backgroundColor: "orange",
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
  },
  contentContainer: {
    backgroundColor: "white",
  },
  item: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderBottomColor: "grey",
    alignItems: "center",
  },
  marginLeft: {
    marginLeft: 5,
  },
  menu: {
    width: 20,
    height: 2,
    backgroundColor: "#111",
    margin: 2,
    borderRadius: 3,
  },
  text: {
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    marginLeft: 100,
    marginRight: 100,
  },
  modalView: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "center",
    justifyContent: "center",
  },
  touchableHighlight: {
    backgroundColor: "white",
    marginVertical: 10,
    alignSelf: "stretch",
    alignItems: "center",
  },
});

export default Fitnessinfo;

