import React, { Component } from "react";
import { StyleSheet, ScrollView, TextInput } from "react-native";
import { ListItem, Button } from "react-native-elements";

class FitnessinfoAdmin extends Component {
  constructor(props) {
    super(props);

    this._deleteCourse = this._deleteCourse.bind(this);
    this.state = {
      courseList: [],
      search: "",
      en: Boolean,
    };
  }

  componentDidMount() {
    // fetch("http://192.168.0.176:9000/api/fitness")
    //fetch("http://192.168.178.23:9000/api/fitness")
    this.setState({
      en: this.props.route.params.en,
    });
    console.log(this.state.en);
    this.loadCourses();
  }

  loadCourses() {
    if (!this.props.route.params.en) {
      fetch("http://192.168.178.23:9000/api/fitness")
        .then((response) => response.json())
        .then((data) => {
          this.setState({ courseList: data });
        })
        .catch((error) => {
          throw error;
        });
      this.props.navigation.addListener("focus", () => {
        fetch("http://192.168.178.23:9000/api/fitness")
          .then((response) => response.json())
          .then((data) => {
            this.setState({ courseList: data });
          })
          .catch((error) => {
            throw error;
          });
      });
    } else {
      fetch("http://192.168.178.23:9000/api/fitness/en")
        .then((response) => response.json())
        .then((data) => {
          this.setState({ courseList: data });
        })
        .catch((error) => {
          throw error;
        });
      this.props.navigation.addListener("focus", () => {
        fetch("http://192.168.178.23:9000/api/fitness/en")
          .then((response) => response.json())
          .then((data) => {
            this.setState({ courseList: data });
          })
          .catch((error) => {
            throw error;
          });
      });
    }
  }

  handleSearch = (text) => {
    if (text) {
      const newData = this.state.courseList.filter(function (item) {
        const itemData = item.name ? item.name.toUpperCase() : "".toUpperCase();
        const textData = text.toUpperCase();
        return itemData.indexOf(textData) > -1;
      });
      this.setState({
        courseList: newData,
        search: text,
      });
    } else {
      this.setState({ search: "" });
      this.loadCourses();
    }
  };

  _deleteCourse(id) {
    const { courseList } = this.state;
    if (!this.props.route.params.en) {
      fetch("http://192.168.178.23:9000/api/fitness/" + id, {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      })
        .then(() => {
          this.setState({
            courseList: courseList.filter((course) => course.id !== id),
          });
          return;
        })
        .catch((error) => {
          throw error;
        });

      console.log("Deleted");
    } else {
      fetch("http://192.168.178.23:9000/api/fitness/en/" + id, {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      })
        .then(() => {
          this.setState({
            courseList: courseList.filter((course) => course.id !== id),
          });
          return;
        })
        .catch((error) => {
          throw error;
        });

      console.log("Deleted");
    }
  }
  editCourseRoute(id, name, price, description) {
    this.props.navigation.navigate("EditCourse", {
      en: false,
      id: course.id,
      name: course.name,
      price: coure.price,
      description: course.description,
    });
  }

  render() {
    const courseList = this.state.courseList;

    if (!this.props.route.params.en) {
      return (
        <ScrollView>
          <TextInput
            style={{ borderWidth: 1 }}
            placeholder="Nach Kurs suchen..."
            onChangeText={(text) => {
              this.handleSearch(text);
            }}
            //value={search}
          />
          <Button
            title="Hinzufügen"
            onPress={() =>
              this.props.navigation.navigate("AddCourse", {
                en: false,
              })
            }
          ></Button>
          {courseList.map((course) => (
            <ListItem key={course.id} bottomDivider>
              <ListItem.Content>
                <ListItem.Title>{"Name"}</ListItem.Title>
                <ListItem.Subtitle>{course.name}</ListItem.Subtitle>
                <ListItem.Title>{"Preis"}</ListItem.Title>
                <ListItem.Subtitle>{course.price}</ListItem.Subtitle>
                <ListItem.Title>{"Beschreibung"}</ListItem.Title>
                <ListItem.Subtitle>{course.description}</ListItem.Subtitle>
                <Button
                  title="Löschen"
                  onPress={() => this._deleteCourse(course.id)}
                ></Button>
                <Button
                  title="Bearbeiten"
                  onPress={() =>
                    this.props.navigation.navigate("EditCourse", {
                      id: course.id,
                      name: course.name,
                      price: course.price,
                      description: course.description,
                      en: true,
                    })
                  }
                ></Button>
              </ListItem.Content>
            </ListItem>
          ))}
        </ScrollView>
      );
    } else {
      return (
        <ScrollView>
          <TextInput
            style={{ borderWidth: 1 }}
            placeholder="Search for a course..."
            onChangeText={(text) => {
              this.handleSearch(text);
            }}
            //value={search}
          />
          <Button
            title="Add"
            onPress={() =>
              this.props.navigation.navigate("AddCourse", {
                en: this.props.route.params.en,
              })
            }
          ></Button>
          {courseList.map((course) => (
            <ListItem key={course.id} bottomDivider>
              <ListItem.Content>
                <ListItem.Title>{"Name"}</ListItem.Title>
                <ListItem.Subtitle>{course.name}</ListItem.Subtitle>
                <ListItem.Title>{"Price"}</ListItem.Title>
                <ListItem.Subtitle>{course.price}</ListItem.Subtitle>
                <ListItem.Title>{"Description"}</ListItem.Title>
                <ListItem.Subtitle>{course.description}</ListItem.Subtitle>
                <Button
                  title="Delete"
                  onPress={() => this._deleteCourse(course.id)}
                ></Button>
                <Button
                  title="Edit"
                  onPress={() =>
                    this.editCourseRoute(
                      course.id,
                      course.name,
                      course.price,
                      course.description
                    )
                  }
                ></Button>
              </ListItem.Content>
            </ListItem>
          ))}
        </ScrollView>
      );
    }
  }
}

const styles = StyleSheet.create({
  header: {
    height: 60,
    backgroundColor: "orange",
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
  },
  contentContainer: {
    backgroundColor: "white",
  },
  item: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderBottomColor: "grey",
    alignItems: "center",
  },
  marginLeft: {
    marginLeft: 5,
  },
  menu: {
    width: 20,
    height: 2,
    backgroundColor: "#111",
    margin: 2,
    borderRadius: 3,
  },
  text: {
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    marginLeft: 10,
  },
  text1: {
    textAlign: "left",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "black",
    // marginLeft: "auto",
  },
  text2: {
    textAlign: "center",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "blue",
    marginLeft: "auto",
  },
  text3: {
    textAlign: "right",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "orange",
    marginLeft: "auto",
  },

  textInput: {
    width: "90%",
    marginLeft: 10,
    marginRight: 10,
    marginBottom: 30,
    borderColor: "gray",
    borderBottomWidth: 2,
    fontSize: 16,
  },
  modalView: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "center",
    justifyContent: "center",
  },
  touchableHighlight: {
    backgroundColor: "white",
    marginVertical: 10,
    alignSelf: "stretch",
    alignItems: "center",
  },
});

export default FitnessinfoAdmin;
