import React, { Component } from "react";

import { StyleSheet, View, Text, Button, TextInput } from "react-native";
import { Agenda } from "react-native-calendars";
import { SearchBar } from "react-native-elements";

export default class Fitnessplan extends Component {
  constructor(props) {
    super(props);

    //this.filterCalendar = this.filterCalendar.bind(this);
    this.searchText = this.searchText.bind(this);

    this.loadEvents = this.loadEvents.bind(this);
    this.state = {
      events: [],
      eventsFormatted: {},
      search: "",
    };
  }

  componentDidMount() {
    this.loadEvents();
    this.focusListener = this.props.navigation.addListener("focus", () =>
      this.loadEvents()
    );
  }

  componentWillUnmount() {
    this.focusListener();
  }
  updateSearch = (text) => {
    this.setState({
      search: text,
    });
  };
  /*filterCalendar = () => {
    var eventsFormatted = this.state.eventsFormatted;
    const search = this.state.search;
    const filtered = Object.keys(eventsFormatted)
      .filter((name) => search.includes(name))
      .reduce((obj, name) => {
        obj[name] = eventsFormatted[name];
        console.log(obj[name]);
        return obj;
      }, {});
    this.setState({ eventsFormatted: filtered });
    /*this.setState({
      events: events.filter((event) => event.name == search),
    });
    return this.state.events;
    console.log(this.state.events);
    console.log(this.state.search);*/
  //};

  searchText = () => {
    console.log("hier");
    const events = this.state.events;
    const search = this.state.search;
    if (events.name.includes(search)) {
      this.setState({
        events: events.filter((event) => event.name == search),
      });
      console.log(events);
      this.showSearchResults();
    }
  };

  showSearchResults() {
    const events = this.state.events;
    var eventsFormatted = {};
    events.map((event) => {
      let day = event.date; //.toDate().toISOString().split("T")[0]; // Format to YYYY-MM-DD
      if (eventsFormatted[day]) {
        eventsFormatted[day].push(event);
      } else {
        eventsFormatted[day] = [event];
      }
    });
    this.setState({
      eventsFormatted: eventsFormatted,
    });
    console.log(`eventsFormatted: ${JSON.stringify(eventsFormatted)}`);
  }
  loadEvents = (day) => {
    //Charge les items du mois
    fetch("http://192.168.178.23:9000/api/fitnessevents")
      .then((response) => response.json())
      .then((events) => {
        console.log(`events: ${JSON.stringify(events)}`);
        return events;
      })
      .then((events) => {
        var eventsFormatted = {};
        if (events.length) {
          events.map((event) => {
            let day = event.date; //.toDate().toISOString().split("T")[0]; // Format to YYYY-MM-DD

            if (eventsFormatted[day]) {
              eventsFormatted[day].push(event);
            } else {
              eventsFormatted[day] = [event];
            }
          });
          this.setState({
            eventsFormatted: eventsFormatted,
          });
          console.log(`eventsFormatted: ${JSON.stringify(eventsFormatted)}`);
        }
      });
  };

  renderItem(item) {
    // console.log(item.name);
    const height = "150";
    return (
      <View style={{ flex: 1, justifyContent: "center" }}>
        <Text>{item.name}</Text>
      </View>
    );
  }

  renderEmptyDate() {
    return (
      <View>
        <Text>This is empty date!</Text>
      </View>
    );
  }
  /* renderDay() {
    return (
      <View>
        <Button title="Add"></Button>
      </View>
    );
  }*/

  render() {
    const eventsFormatted = this.state.eventsFormatted;

    return (
      <View style={{ flex: 1, backgroundColor: "white", paddingBottom: 30 }}>
        <TextInput
          placeholder="Type Here..."
          onChangeText={(text) => {
            this.updateSearch(text);
          }}
          //value={search}
        />
        <Button title="Search" onPress={() => this.searchText()}></Button>
        <Agenda
          //style={styles.calendar}
          selected={"2020-09-04"}
          items={eventsFormatted}
          loadItemsForMonth={this.loadEvents}
          renderItem={this.renderItem.bind(this)}
          // renderDay={this.renderDay.bind(this)}
          renderEmptyData={() => null}
          renderEmptyDate={this.renderEmptyDate.bind(this)}
          //loadEvents={(day) => this.loadEvents(day)}
          events={this.state.eventsFormatted}
          onPressEvent={(event) =>
            this.props.navigation.navigate("Login", { event })
          }
        />
      </View>
    );
  }
}
