import React, { Component } from "react";
import { Button, View, Text, TouchableOpacity, ListView } from "react-native";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
} from "react-native-table-component";
//import { DataTable, Cell } from "react-native-data-table";

import courseList from "./data/courses.json";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);

    this.state = {
      courseList: courseList,
      HeadTable: ["Name", "Preis", "Beschreibung"],
    };
  }
  _deleteCourse(id) {
    const { courseList } = this.state;
    this.setState({
      courseList: courseList.filter((fitness) => fitness.id !== id),
    });
    console.log("Deleted");
    return;
  }
  render() {
    const state = this.state;
    const courseList = this.state.courseList.map((fitness) => [
      fitness.name,
      fitness.price,
      fitness.description,
    ]);
    const element = (data, index) => (
      <TouchableOpacity onPress={() => this._deleteCourse}>
        <View>
          <Text>button</Text>
        </View>
      </TouchableOpacity>
    );

    return (
      <ListView>
        <renderHeader data={state.HeadTable} />
        {courseList.map((fitness, index) => (
          <renderRow key={index} data={fitness} />
        ))}
      </ListView>
    );
  }
}

export default Fitnessinfo;

