import React, { Component } from "react";
import {
  Button,
  View,
  Text,
  ListView,
  StyleSheet,
  FlatList,
  Section,
  TouchableHighlight,
  TextInput,
} from "react-native";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from "react-native-table-component";
import Modal from "react-native-modal";

import courseList from "./data/courses.json";
import FitnessInfoFunction from "./FitnessInfoFunction";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tableHead: ["Name", "Preis", "Beschreibung"],
      courseList: courseList,
      isModalVisible: false,
    };
  }

  componentDidMount() {}
  toggleModal = () => {
    this.setState({ isModalVisible: !this.state.isModalVisible });
  };

  _addCourse() {
    console.log("Added");
  }
  _deleteCourse(id) {
    const { courseList } = this.state;
    this.setState({
      courseList: courseList.filter((fitness) => fitness.id !== id),
    });
    console.log("Deleted");
    return;
  }
  renderItem = ({ item }) => (
    <View style={styles.item}>
      <Text style={styles.text1}>{item.name}</Text>
      <Text style={styles.text2}>{item.price}</Text>
      <Text style={styles.text3}>{item.description}</Text>
    </View>
  );
  render() {
    return (
      <View style={styles.contentContainer}>
        <FlatList
          data={courseList}
          keyExtractor={(item) => item.name}
          numColumns={1}
          renderItem={this.renderItem}
        />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  header: {
    height: 60,
    backgroundColor: "orange",
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
  },
  contentContainer: {
    backgroundColor: "white",
  },
  item: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderBottomColor: "grey",
    alignItems: "center",
  },
  marginLeft: {
    marginLeft: 5,
  },
  menu: {
    width: 20,
    height: 2,
    backgroundColor: "#111",
    margin: 2,
    borderRadius: 3,
  },
  text: {
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    marginLeft: 10,
  },
  text1: {
    textAlign: "left",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "black",
    // marginLeft: "auto",
  },
  text2: {
    textAlign: "center",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "blue",
    marginLeft: "auto",
  },
  text3: {
    textAlign: "right",
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    color: "orange",
    marginLeft: "auto",
  },

  textInput: {
    width: "90%",
    marginLeft: 10,
    marginRight: 10,
    marginBottom: 30,
    borderColor: "gray",
    borderBottomWidth: 2,
    fontSize: 16,
  },
  modalView: {
    flex: 1,
    backgroundColor: "white",
    alignItems: "center",
    justifyContent: "center",
  },
  touchableHighlight: {
    backgroundColor: "white",
    marginVertical: 10,
    alignSelf: "stretch",
    alignItems: "center",
  },
});

export default Fitnessinfo;

