import React, { Component } from "react";

import { View, Text, TextInput, Button } from "react-native";
import DatePicker from "react-native-datepicker";
import DropDownPicker from "react-native-dropdown-picker";

export default class EditCalendar extends Component {
  constructor(props) {
    super(props);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      date: "",
      label: "",
      items: [],
    };
  }

  componentDidMount() {
    // fetch("http://192.168.0.176:9000/api/fitness")
    //fetch("http://192.168.178.23:9000/api/fitness")
    fetch("http://192.168.178.23:9000/api/dropdown")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ items: data });
      });
    this.setState({
      label: this.props.route.params.name,
      date: this.props.route.params.date,
    });
  }
  onChangeDate = (text) => {
    this.setState({
      date: text,
    });
  };
  onChangeName = (text) => {
    this.setState({
      name: text,
    });
  };

  //ToDo id in die URL bekommen
  handleSubmit() {
    //id = this.props.route.params.course.id;
    fetch(
      "http://192.168.178.23:9000/api/fitnessevent/" +
        this.props.route.params.id,
      {
        method: "PUT",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          date: this.state.date,
          name: this.state.label,
        }),
      }
    )
      .then((res) => res.json())
      .catch((error) => {
        throw error;
      });
    this.props.navigation.goBack("FitnessplanAdmin");
    console.log("Edited");
  }

  render() {
    return (
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <Text>Edit Screen</Text>
        <DropDownPicker
          //key={item.value}
          items={this.state.items}
          controller={(instance) => (this.controller = instance)}
          onChangeList={(items, callback) => {
            this.setState(
              {
                items: items, // items: items
              },
              callback
            );
          }}
          // defaultValue={this.state.label}
          containerStyle={{ height: 40 }}
          style={{ backgroundColor: "#fafafa" }}
          itemStyle={{
            justifyContent: "flex-start",
          }}
          dropDownStyle={{ backgroundColor: "#fafafa" }}
          onChangeItem={(item) =>
            this.setState({
              name: item.value,
            })
          }
        />
        <TextInput
          style={{
            height: 40,
            borderColor: "gray",
            borderWidth: 1,
            width: 300,
          }}
          onChangeText={(text) => this.onChangeName(text)}
          defaultValue={this.state.date}
        />
        <Button title="Edit" onPress={() => this.handleSubmit()}></Button>
      </View>
    );
  }
}

