// App.js

import React, { Component } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { View, Text } from "react-native";
import ThemeContext from "./screens/theme-context";
import { Button } from "react-native-elements";

import Home from "./screens/Home";
import Fitnessplan from "./screens/Fitnessplan";
import Fitnessinfo from "./screens/Fitnessinfo";
import Login from "./screens/Login";
import EditCourse from "./screens/admin/EditCourse";
import AddCourse from "./screens/admin/AddCourse";

//import credentials from "./screens/data/credentials.json";

const Stack = createStackNavigator();

export default class App extends Component {
  constructor(props) {
    super(props);

    //this.handleLogin = this.handleLogin.bind(this);

    this.state = {
      username: "",
      password: "",
      token: "",
      isLoggedIn: Boolean,
    };
  }

  onChangeUsername = (text) => {
    this.setState({
      username: text,
    });
  };
  onChangePassword = (text) => {
    this.setState({
      password: text,
    });
  };

  handleLogin() {
    //console.log(this.props.history);
    fetch("http://192.168.0.176:9000/api/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password,
      }),
    })
      .then((res) => res.json())
      .then((response) => {
        const token = response.token;
        this.setState({
          token: token,
        });
        localStorage.setItem("token", this.state.token);
        if (token != null) {
          this.setState({
            isLoggedIn: true,
          });
          console.log("Eingeloggt");
        } else {
          console.log("Nicht geklappt");
        }
      })

      .catch((err) => console.log(err));
    //this.props.navigation.navigate("Home");
    /* this.props.navigation.reset({
      index: 0,
      routes: [{ name: "Home" }],
    });*/
  }

  handleLogout() {
    localStorage.removeItem("token");
    this.setState({
      isLoggedIn: false,
    });
    console.log("Ausgeloggt");
  }

  render() {
    return (
      <NavigationContainer>
        <Stack.Navigator
          screenOptions={{
            headerTitleAlign: "center",
            headerStyle: {
              backgroundColor: "#621FF7",
            },
            headerTintColor: "#fff",
            headerTitleStyle: {
              fontWeight: "bold",
            },
          }}
        >
          <Stack.Screen
            name="Home"
            component={Home}
            options={{
              title: "Home",
            }}
          />
          <Stack.Screen
            name="Fitnessplan"
            component={Fitnessplan}
            options={{ title: "Fitnessplan" }}
          />
          <Stack.Screen
            name="Fitnessinfo"
            component={Fitnessinfo}
            options={{ title: "Fitnessinfo" }}
          />
          <Stack.Screen
            name="Login"
            component={Login}
            options={{ title: "Login" }}
            initialParams={{
              handleLogin: this.handleLogin.bind(this),
              handleLogout: this.handleLogout.bind(this),
              onChangeUsername: this.onChangeUsername.bind(this),
              onChangePassword: this.onChangePassword.bind(this),
            }}
          />
          <Stack.Screen
            name="AddCourse"
            component={AddCourse}
            options={{ title: "AddCourse" }}
          />
          <Stack.Screen
            name="EditCourse"
            component={EditCourse}
            options={{ title: "EditCourse" }}
          />
        </Stack.Navigator>
      </NavigationContainer>
    );
  }
}
