import React, { Component } from "react";
import { Button, View, Text } from "react-native";
import credentials from "./data/credentials.json";

class Login extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.handleLogin = this.handleLogin.bind(this);

    this.state = {
      credentials: credentials,
      user: "",
      pw: "",
      isLoggedIn: Boolean,
      username: "",
      password: "",
    };
  }
  componentDidMount() {
    let results = credentials.map((credential) => {
      const user = credential.username;
      const pw = credential.password;
      console.log(user);
      this.setState({ user: user });
      this.setState({ pw: pw });
    });
  }
  onChangeUsername(e) {
    this.setState({
      username: e.target.value,
    });
  }
  onChangePassword(e) {
    this.setState({
      password: e.target.value,
    });
  }
  handleLogin() {
    if (this.state.username === user && this.state.password === pw) {
      console.log("Eingeloggt");
    } else {
      console.log("Falsche Daten");
    }
  }
  render() {
    return (
      <View>
        <div>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              className="form-control"
              id="username"
              required
              onChange={this.onChangeUsername}
              name="username"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Passwort</label>
            <input
              type="password"
              className="form-control"
              id="password"
              required
              onChange={this.onChangePassword}
              name="password"
            />
          </div>
          <button onClick={this.handleLogin} className="btn btn-success">
            Login
          </button>
          <button onClick={this.props.handleLogout} className="btn btn-success">
            Logout
          </button>
        </div>
      </View>
    );
  }
}

export default Login;
