import React, { Component } from "react";
import {
  Button,
  View,
  Text,
  TouchableOpacity,
  ListView,
  StyleSheet,
  FlatList,
  Section,
  Modal,
  TouchableHighlight,
  TextInput,
  ScrollView,
} from "react-native";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
} from "react-native-table-component";
import courseList from "./data/courses.json";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      myArray: [],
      tableHead: ["Name", "Preis", "Beschreibung"],
      widthArr: [40, 100, 80, 100, 120, 140, 160, 180, 200],
    };
  }

  componentDidMount() {
    this.setState({ myArray: courseList });
  }

  render() {
    const tableData = this.state.myArray.map((record) => [
      record.name,
      record.price,
      record.description,
    ]);
    return (
      <View style={styles1.container}>
        <ScrollView horizontal={true}>
          <View>
            <Table borderStyle={{ borderWidth: 1, borderColor: "#C1C0B9" }}>
              <Row
                data={this.state.tableHead}
                widthArr={this.state.widthArr}
                style={styles1.header}
                textStyle={styles1.text}
              />
            </Table>
            <ScrollView style={styles1.dataWrapper}>
              <Table borderStyle={{ borderWidth: 1, borderColor: "#C1C0B9" }}>
                {tableData.map((rowData, index) => (
                  <Row
                    key={index}
                    data={rowData}
                    widthArr={this.state.widthArr}
                    style={[
                      styles1.row,
                      index % 2 && { backgroundColor: "#F7F6E7" },
                    ]}
                    textStyle={styles1.text}
                  />
                ))}
              </Table>
            </ScrollView>
          </View>
        </ScrollView>
      </View>
    );
  }
}

const styles1 = StyleSheet.create({
  container: { flex: 1, padding: 16, paddingTop: 30, backgroundColor: "#fff" },
  header: { height: 50, backgroundColor: "#537791" },
  text: { textAlign: "center", fontWeight: "100" },
  dataWrapper: { marginTop: -1 },
  row: { height: 40, backgroundColor: "#E7E6E1" },
});

export default Fitnessinfo;

