import React, { Component } from "react";

import {
  StyleSheet,
  View,
  FlatList,
  Dimensions,
  ActivityIndicator,
  Platform,
  Text,
  TouchableOpacity,
  Button,
} from "react-native";
import { Calendar, CalendarList, Agenda } from "react-native-calendars";

export default class Fitnessinfo extends React.Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);
    this.state = {
      events: [],
      eventsFormatted: {},
    };
  }

  componentDidMount() {
    // this.loadEvents();
    this.props.navigation.addListener("focus", () => {
      fetch("http://192.168.178.23:9000/api/fitnessevents")
        .then((response) => response.json())
        .then((events) => {
          console.log(`events: ${JSON.stringify(events)}`);
          return events;
        })
        .then((events) => {
          var eventsFormatted = {};
          if (events.length) {
            events.map((event) => {
              let day = event.date; //.toDate().toISOString().split("T")[0]; // Format to YYYY-MM-DD
              if (eventsFormatted[day]) {
                eventsFormatted[day].push(event);
              } else {
                eventsFormatted[day] = [event];
              }
            });
            this.setState({
              eventsFormatted: eventsFormatted,
            });
            console.log(`eventsFormatted: ${JSON.stringify(eventsFormatted)}`);
          }
        });
    });
  }

  /*componentWillUnmount() {
    this.focusListener();
  }*/

  loadEvents = (day) => {
    //Charge les items du mois
    fetch("http://192.168.178.23:9000/api/fitnessevents")
      .then((response) => response.json())
      .then((events) => {
        console.log(`events: ${JSON.stringify(events)}`);
        return events;
      })
      .then((events) => {
        var eventsFormatted = {};
        if (events.length) {
          events.map((event) => {
            let day = event.date; //.toDate().toISOString().split("T")[0]; // Format to YYYY-MM-DD
            if (eventsFormatted[day]) {
              eventsFormatted[day].push(event);
            } else {
              eventsFormatted[day] = [event];
            }
          });
          this.setState({
            eventsFormatted: eventsFormatted,
          });
          console.log(`eventsFormatted: ${JSON.stringify(eventsFormatted)}`);
        }
      });
  };

  //to do: delete by unique id
  _deleteCourse(name) {
    const { eventsFormatted } = this.state;
    fetch("http://192.168.178.23:9000/api/fitnessevents/" + name, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then(() => {
        this.setState({
          eventsFormatted: eventsFormatted.filter(
            (event) => event.name !== name
          ),
        });
        return;
      })
      .catch((error) => {
        throw error;
      });
    //this.loadEvents;
    console.log("Deleted");
  }

  renderItem(item) {
    // console.log(item.name);
    return (
      <View>
        <Text>{item.name}</Text>
        <Button title="Edit"></Button>
        <Button
          title="Delete"
          onPress={() => this._deleteCourse(item.name)}
        ></Button>
      </View>
    );
  }

  renderEmptyDate() {
    return (
      <View>
        <Text>This is empty date!</Text>
      </View>
    );
  }
  /* renderDay() {
    return (
      <View>
        <Button title="Add"></Button>
      </View>
    );
  }*/

  render() {
    return (
      <View style={{ flex: 1, backgroundColor: "white", paddingBottom: 30 }}>
        <Button
          title="Add"
          onPress={() => this.props.navigation.navigate("AddToCalendar")}
        ></Button>
        <Agenda
          //style={styles.calendar}
          selected={"2020-09-04"}
          items={this.state.eventsFormatted}
          loadItemsForMonth={this.loadEvents.bind(this)}
          renderItem={this.renderItem.bind(this)}
          // renderDay={this.renderDay.bind(this)}
          renderEmptyData={() => null}
          renderEmptyDate={this.renderEmptyDate.bind(this)}
          //loadEvents={(day) => this.loadEvents(day)}
          events={this.state.eventsFormatted}
          onPressEvent={(event) =>
            this.props.navigation.navigate("Login", { event })
          }
        />
      </View>
    );
  }
}
