import React, { Component } from "react";
import DropDownPicker from "react-native-dropdown-picker";

import { View, TextInput, ScrollView } from "react-native";
import { Button } from "react-native-elements";

//import courseList from "./data/courses.json";

class EditCalendar extends Component {
  constructor(props) {
    super(props);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.state = {
      items: [],
      item: "",
      name: "",
      date: "",
    };
    this.controller;
  }
  componentDidMount() {
    if (!this.props.route.params.en) {
      // fetch("http://192.168.0.176:9000/api/fitness")
      //fetch("http://192.168.178.23:9000/api/fitness")
      fetch("http://192.168.178.23:9000/api/dropdown")
        .then((response) => response.json())
        .then((data) => {
          this.setState({ items: data });
        });
      this.setState({
        date: this.props.route.params.date,
      });
    } else {
      fetch("http://192.168.178.23:9000/api/dropdown/en")
        .then((response) => response.json())
        .then((data) => {
          this.setState({ items: data });
        });
      this.setState({
        date: this.props.route.params.date,
      });
    }
  }

  onChangeDate = (text) => {
    this.setState({
      date: text,
    });
  };

  handleSubmit() {
    //id = this.props.route.params.course.id;
    if (!this.props.route.params.en) {
      fetch(
        "http://192.168.178.23:9000/api/fitnessevent/" +
          this.props.route.params.id,
        {
          method: "PUT",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            date: this.state.date,
            name: this.state.name,
          }),
        }
      )
        .then((res) => res.json())
        .catch((error) => {
          throw error;
        });
      this.props.navigation.goBack("FitnessplanAdmin");
      console.log("Bearbeitet");
    } else {
      fetch(
        "http://192.168.178.23:9000/api/fitnessevent/en/" +
          this.props.route.params.id,
        {
          method: "PUT",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            date: this.state.date,
            name: this.state.name,
          }),
        }
      )
        .then((res) => res.json())
        .catch((error) => {
          throw error;
        });
      this.props.navigation.goBack("FitnessplanAdmin");
      console.log("Edited");
    }
  }

  render() {
    const item = (
      <View>
        {this.state.items.map((item) => (
          <View key={item.value}></View>
        ))}
      </View>
    );
    return (
      <ScrollView>
        <DropDownPicker
          items={this.state.items}
          controller={(instance) => (this.controller = instance)}
          onChangeList={(items, callback) => {
            this.setState(
              {
                items: items,
              },
              callback
            );
          }}
          //defaultValue={this.state.countries}
          //containerStyle={{ flex:  }}
          dropDownMaxHeight={300}
          style={{ backgroundColor: "#fafafa" }}
          itemStyle={{
            justifyContent: "flex-start",
          }}
          //containerStyle={{ height: 100 }}
          //style={{ paddingVertical: 100 }}
          // dropDownStyle={{ backgroundColor: "#fafafa" }}
          dropDownStyle={{ marginTop: 2 }}
          onChangeItem={(item) =>
            this.setState({
              name: item.value,
            })
          }
        />
        <TextInput
          style={{
            height: 40,
            borderColor: "gray",
            borderWidth: 1,
            width: 300,
          }}
          onChangeText={(text) => this.onChangeDate(text)}
          //placeholder="2020-09-04"
          defaultValue={this.state.date}
        />
        <Button title="Edit" onPress={() => this.handleSubmit()}></Button>
      </ScrollView>
    );
  }
}

export default EditCalendar;
