import React, { Component } from "react";
import {
  Button,
  View,
  Text,
  TouchableOpacity,
  ListView,
  StyleSheet,
  FlatList,
  Section,
} from "react-native";

import courseList from "./data/courses.json";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);

    this.state = {
      courseList: courseList,
      HeadTable: ["Name", "Preis", "Beschreibung"],
    };
  }
  _deleteCourse(index) {
    const { courseList } = this.state;
    this.setState({
      courseList: courseList.filter((fitness) => fitness.id !== id),
    });
    console.log("Deleted");
    return;
  }
  renderItemName = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.marginLeft}>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
      </View>
      <Text style={styles.text}>{item.name}</Text>
    </View>
  );
  renderItemPrice = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.marginLeft}>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
      </View>
      <Text style={styles.text}>{item.price}</Text>
    </View>
  );
  renderItemDescription = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.marginLeft}>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
        <View style={[styles.menu, { backgroundColor: item.color }]}></View>
      </View>
      <Text style={styles.text}>{item.description}</Text>
    </View>
  );

  render() {
    return (
      <View style={styles.contentContainer}>
        <View style={styles.header}>
          <Text style={styles.headerText}>Name</Text>
        </View>
        <FlatList
          horizontal={true}
          data={courseList}
          keyExtractor={(item) => item.name}
          renderItem={this.renderItemName}
        />
        <View style={styles.header}>
          <Text style={styles.headerText}>Name</Text>
        </View>
        <FlatList
          horizontal={true}
          data={courseList}
          keyExtractor={(item) => item.name}
          renderItem={this.renderItemPrice}
        />
        <View style={styles.header}>
          <Text style={styles.headerText}>Name</Text>
        </View>
        <FlatList
          horizontal={true}
          data={courseList}
          keyExtractor={(item) => item.name}
          renderItem={this.renderItemDescription}
        />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  header: {
    height: 60,
    backgroundColor: "orange",
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
  },
  contentContainer: {
    backgroundColor: "white",
  },
  item: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderBottomColor: "grey",
    alignItems: "center",
  },
  marginLeft: {
    marginLeft: 5,
  },
  menu: {
    width: 20,
    height: 2,
    backgroundColor: "#111",
    margin: 2,
    borderRadius: 3,
  },
  text: {
    marginVertical: 30,
    fontSize: 20,
    fontWeight: "bold",
    marginLeft: 10,
  },
});

export default Fitnessinfo;

