import React, { Component } from "react";
import { Button, View, Text, TouchableOpacity, ListView } from "react-native";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
} from "react-native-table-component";
//import { DataTable, Cell } from "react-native-data-table";

import courseList from "./data/courses.json";

class Fitnessinfo extends Component {
  constructor(props) {
    super(props);
    this._deleteCourse = this._deleteCourse.bind(this);

    const columns = [
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "Preis",
        accessor: "preis",
      },
      {
        Header: "Beschreibung",
        accessor: "Beschreibung",
      },
      {
        Header: "Button",
        accessor: "button",
        Cell: ({ cell }) => (
          <Button title="delete" onPress={this._deleteCourse}></Button>
        ),
      },
    ];

    this.state = {
      courseList: courseList,
      HeadTable: ["Name", "Preis", "Beschreibung", "4"],
    };
  }
  _deleteCourse(id) {
    const { courseList } = this.state;
    this.setState({
      courseList: courseList.filter((fitness) => fitness.id !== id),
    });
    console.log("Deleted");
    return;
  }
  render() {
    const state = this.state;
    const courseList = this.state.courseList.map((fitness) => [
      fitness.name,
      fitness.price,
      fitness.description,
      console.log(fitness.name),
    ]);
    return (
      <View>
        <Table borderStyle={{ borderWidth: 1, borderColor: "#000000" }}>
          <Rows data={state.HeadTable} />
          {this.state.courseList.map((fitness) => (
            <TableWrapper key={fitness.id}>
              <Row data={fitness.name} />
              <Row data={fitness.price} />
              <Row data={fitness.description} />
            </TableWrapper>
          ))}
        </Table>
      </View>
    );
  }
}

export default Fitnessinfo;

