import React, { Component } from "react";

import {
  View,
  Text,
  ListView,
  StyleSheet,
  FlatList,
  Section,
  TouchableHighlight,
  TextInput,
} from "react-native";
import { Button } from "react-native-elements";

import Fitnessinfo from "../Fitnessinfo";

export default class AddCourse extends Component {
  constructor(props) {
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      //courseList: {
      id: null,
      name: "",
      price: "",
      description: "",
      submitted: false,
      // },
    };
  }

  componentDidMount() {}

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }
  onChangePrice = (text) => {
    this.setState({
      price: text,
    });
  };
  onChangeDescription(e) {
    this.setState({
      description: e.target.value,
    });
  }
  handleSubmit() {
    //fitness.preventDefault();
    fetch("http://192.168.0.176:9000/api/addCourse", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: this.state.name,
        price: this.state.price,
        description: this.state.description,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((err) => console.log(err));
    this.props.navigation.reset({
      routes: [{ name: "Fitnessinfo" }],
    });
  }
  onSuccess() {
    this.props.navigation.navigate("Fitnessinfo");
  }
  render() {
    return (
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <Text>Add Screen</Text>
        <TextInput
          style={{
            height: 40,
            borderColor: "gray",
            borderWidth: 1,
          }}
          onChange={this.onChangeName}
          //value={value}
        />
        <TextInput
          style={{
            height: 40,
            borderColor: "gray",
            borderWidth: 1,
          }}
          onChangeText={(text) => this.onChangePrice(text)}

          //value={value}
        />
        <TextInput
          style={{
            height: 40,
            borderColor: "gray",
            borderWidth: 1,
          }}
          onChange={this.onChangeDescription}
          //value={value}
        />
        <Button title="Add" onPress={() => this.handleSubmit()}></Button>
      </View>
    );
  }
}

