// App.js

import React, { Component } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { Button, View, Text } from "react-native";

import Home from "./screens/Home";
import Fitnessplan from "./screens/Fitnessplan";
import Fitnessinfo from "./screens/Fitnessinfo";
import Login from "./screens/Login";
import EditCourse from "./screens/admin/EditCourse";
import AddCourse from "./screens/admin/AddCourse";

import credentials from "./screens/data/credentials.json";

const Stack = createStackNavigator();

//Login und Fitnessinfo funktioniert bislang nur auf dem Desktop (nicht mobile)
function NavStack(navigation) {
  return (
    <ThemeContext.Provider value={this.state}>
      <Stack.Navigator
        screenOptions={{
          headerTitleAlign: "center",
          headerStyle: {
            backgroundColor: "#621FF7",
          },
          headerTintColor: "#fff",
          headerTitleStyle: {
            fontWeight: "bold",
          },
        }}
      >
        <Stack.Screen
          name="Home"
          component={Home}
          options={{
            title: "Home",
          }}
        />
        <Stack.Screen
          name="Fitnessplan"
          component={Fitnessplan}
          options={{ title: "Fitnessplan" }}
        />
        <Stack.Screen
          name="Fitnessinfo"
          component={Fitnessinfo}
          options={{ title: "Fitnessinfo" }}
        />
        <Stack.Screen
          name="Login"
          component={Login}
          options={{ title: "Login" }}
        />
        <Stack.Screen
          name="AddCourse"
          component={AddCourse}
          options={{ title: "AddCourse" }}
        />
        <Stack.Screen
          name="EditCourse"
          component={EditCourse}
          options={{ title: "EditCourse" }}
        />
      </Stack.Navigator>
    </ThemeContext.Provider>
  );
}

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      token: "",
      isLoggedIn: Boolean,
    };
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value,
    });
  }
  onChangePassword(e) {
    this.setState({
      password: e.target.value,
    });
  }

  handleLogin() {
    //console.log(this.props.history);
    fetch("http://192.168.0.176:9000/api/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password,
      }),
    })
      .then((res) => res.json())
      .then((response) => {
        const token = response.token;
        this.setState({
          token: token,
        });
        localStorage.setItem("token", this.state.token);
        if (token != null)
          this.setState({
            isLoggedIn: true,
          });
        // history.push("/FitnessInfoAdmin");
        console.log("Eingeloggt");
      })

      .catch((err) => console.log(err));
  }

  handleLogout() {
    localStorage.removeItem("token");
    this.setState({
      isLoggedIn: false,
    });
    console.log("Ausgeloggt");
  }
  render() {
    return (
      <NavigationContainer>
        <NavStack />
      </NavigationContainer>
    );
  }
}
